package conditionalStatements;

public class OddReverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=0;
		for(int i=30;i>=n;i--)
		{
			if(i%2!=0)
			{
				System.out.println("Odd numbers between 30 to 0:"+i);
			
			}
		
		}
		

	}

}
